import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-todo-item',
  templateUrl: './todo-item.component.html',
  styleUrls: ['./todo-item.component.css']
})
export class TodoItemComponent implements OnInit {
  isHovered = false;
  isEditing = false;

  // const button = document.querySelector('.round-button');
  // button.

  // button.addEventListener('click', function() {
  //   this.classList.toggle('checked');
  // });

  constructor(){}

  ngOnInit(){

  }

}
<div class="todo-item d-flex justify-content-between align-items-center"
    (mouseover)="isHovered = true"
    (mouseout)="isHovered = false"
>
    <div class="todo">
        <input type="checkbox" class="round-button">
        <label>Todo Item</label>
    </div>

    <div class="d-flex align-items-center">
        <span 
            class="icon-wrapper text-center-edit" 
            [ngClass]="{'active': isHovered}"
        >
            <i class="eva eva-edit-outline" (click)="isEditing = true"></i>
        </span>

        <span 
            class="icon-wrapper text-center" 
            [ngClass]="{'active': isHovered}"
        >
            <i class="eva eva-close"></i>
        </span>

        <form class="edit-form" *ngIf="isEditing">
            <input type="text" name="editTodo">
        </form>
    </div>
</div>
