import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Todo } from 'src/app/models/todo.model';

@Component({
  selector: 'app-todo-item',
  templateUrl: './todo-item.component.html',
  styleUrls: ['./todo-item.component.css']
})
export class TodoItemComponent implements OnInit {
  @Input() todo!: Todo;
  @Output() changeStatus: EventEmitter<Todo> = new EventEmitter<Todo>();

  isHovered = false;
  isEditing = false;

  constructor(){}

  ngOnInit(){

  }

  changeTodoStatus(){
    this.changeStatus.emit({...this.todo, isCompleted: !this.todo.isCompleted});
  }

}
<div
  class="todo-item d-flex justify-content-between align-items-center"
  (mouseover)="isHovered = true"
  (mouseout)="isHovered = false"
>
  <div class="todo d-flex justify-content-between align-items-center">
    <input
      type="checkbox"
      [id]="todo.id"
      class="round-button"
      [checked]="todo.isCompleted"
      (change)="changeTodoStatus()"
    />
    <label [for]="todo.id">{{ todo.content }}</label>
  </div>

  <div class="d-flex align-items-center">
    <span
      class="icon-wrapper text-center-edit"
      [hidden]="todo.isCompleted"
      [ngClass]="{ active: isHovered }"
    >
      <i class="eva eva-edit-outline" (click)="isEditing = true"></i>
    </span>

    <span class="icon-wrapper text-center" [ngClass]="{ active: isHovered }">
      <i class="eva eva-close"></i>
    </span>

    <form class="edit-form" *ngIf="isEditing">
      <input type="text" name="editTodo" />
    </form>
  </div>
</div>
